        }
      });

      return res.status(200).json({
        status: 200,
        message: 'Payment successful',
        response
      });
    }

    return res.status(400).json({
      status: 400,
      error: 'Payment verification failed',
      response
    });

  } catch (error) {
    return next(error);
  }
}

